import React,{useEffect, useRef,useState,useContext} from 'react'
import './../shared/tour-details.css'
import { Container,Row,Col,Form,ListGroup } from 'reactstrap'
import { useParams } from 'react-router-dom'
import calculateAvgRating from '../utils/avgRating'
import avatar from '../assets/images/avatar.jpg'
import Booking from '../component/Booking/Booking'
import Newsletter from '../shared/Newsletter'
import useFetch from './../hooks/useFetch'
import {BASE_URL} from './../utils/config'
import {AuthContext} from './../context/AuthContext'
const TourDetails = () => {
  const {id} =useParams()
//static data 
const reviewMsgRef=useRef('')
const [tourRating,setTourRating]=useState(null);
const {user} = useContext(AuthContext)

//fetch data from database
  const {data:tour, loading,error}=useFetch(`${BASE_URL}/tours/${id}`)
  //destructure properties from tour object

  const {photo,title,desc,address,price,reviews,city,distance,maxGroupSize}=tour;

 const{totalRating,avgRating}=calculateAvgRating(reviews);

 //format date
 const options= {day:'numeric',month:'long', year:'numeric'};

 //submit request to the server
 const submitHandler=async e=>{
  e.preventDefault()
  const reviewText = reviewMsgRef.current.value;

  try{
    if (!user || user === undefined || user ===null){
      alert('Please sign up');
    }
    const reviewObj = {
      username:user.username,
      reviewText,
      rating:tourRating
    }
    const res = await fetch(`${BASE_URL}/review/${id}`,{
      method:'POST',
      headers:{
        'content-type':'application/json'
      },
      credentials:'include',
      body:JSON.stringify(reviewObj)
    })
    const result=await res.json()
    if(!res.ok) 
      {
        return alert(result.message);
      }
      alert(result)

  }catch(err){
    alert(err.message)
  }
 };

 useEffect(()=>{
  window.scrollTo(0,0)
 },[tour])
  return (
    <>
    <section>
      <Container>
        {loading && <h4 className='text-center pt-5'>Loading.....</h4>}
        {error && <h4 className='text-center pt-5'>{error}</h4>}
        {
          !loading && !error &&         <Row>
          <Col lg='8'>
            <div className="tour__content">
              <img src={photo} alt="" />
              <div className="tour__info">
                <h2>{title}</h2>
                <div className='d-flex align-items-center gap-5'>
            <span className="tour__rating d-flex align-items-center gap-1" >
            <i class="ri-star-fill" style={{'color':"var(--secondary-color)"}}></i>
            {avgRating ===  0 ? null : avgRating}{totalRating===0 ?(
              "Not Rated"
              ):(
              <span>({reviews?.length})</span>)} 
            </span>
                <span>
                <i class="ri-map-pin-fill"></i>{address}
                </span>
                </div>
             <div className="tour__extra-details">
                <span>
                <i class="ri-map-pin-fill"></i>{city}
                </span>
                <span>
                <i class="ri-money-dollar-circle-line"></i>${price} / per person
                </span>
                <span>
                <i class="ri-map-pin-time-line"></i>{distance} km
                </span>
                <span>
                <i class="ri-group-line"></i>{maxGroupSize} people
                </span>
                </div>
                <h5>Description</h5>
                <p>{desc}</p>
                </div>
                {/*=============tour review section========== */}
                <div className="tour__reviews mt-4">
                  <h4>Reviews ({reviews?.length} reviews)</h4>
                  <Form onSubmit={submitHandler}>
                    <div className="d-flex aligm-items-center gap-3 mb-4 rating__group">
                      <span onClick={()=>setTourRating(1)}> 
                       1 <i class="ri-star-s-fill"></i></span>
                        <span  onClick={()=>setTourRating(2)}>
                        2 <i class="ri-star-s-fill"></i></span>
                        <span onClick={()=>setTourRating(3)}>
                        3 <i class="ri-star-s-fill"></i></span>
                        <span onClick={()=>setTourRating(4)}>
                        4 <i class="ri-star-s-fill"></i></span>
                        <span onClick={()=>setTourRating(5)}>
                        5 <i class="ri-star-s-fill"></i></span>
 

                    </div>                     
                    <div className="review__input">
                      <input type="text" ref={reviewMsgRef}  placeholder='share your thoughts'/>
                      <button className="btn primary__btn text-white" type='submit'>Submit</button>
                    </div>
                  </Form>
                  <ListGroup className="user__reviews">
                    {
                      reviews?.map(review=>(
                        <div className="review__item">
                          <img src={avatar} alt="" />
                          <div className="w-100">
                            <div className="d-flex align-items-center justify-content-between">
                              <div>
                                <h5>{review.username}</h5>
                                <p>{new Date('04-05-2024').toLocaleDateString('en-US',options) }</p>
                              </div>
                              <span class="d-flex align-items-center">
                                {review.rating} <i class="ri-star-s-fill"></i>
                              </span>
                            </div>
                            <h6>{review.reviewText}</h6>
                          </div>
                        </div>
                      ))
                    }

                  </ListGroup>
                </div>
               {/*=============tour review section end========== */}
              </div>
            
          </Col>
          <Col lg='4'>
            <Booking tour={tour} avgRating={avgRating}/>

          </Col>
        </Row>
        }
      </Container>
    </section>
    <Newsletter/>
    </>
  )
}

export default TourDetails
