import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../utils/config';

const UpdateTour = () => {
  const { id } = useParams();
  const [tourData, setTourData] = useState(null);

  useEffect(() => {
    const fetchTourData = async () => {
      try {
        const response = await fetch(`${BASE_URL}/tours/${id}`);
        if (response.ok) {
          const data = await response.json();
          setTourData(data);
        } else {
          throw new Error('Failed to fetch tour data');
        }
      } catch (error) {
        console.error('Error fetching tour data:', error.message);
        // Handle error fetching data
      }
    };

    fetchTourData();
  }, [id]);

  if (!tourData) {
    return <div>Loading...</div>;  // Placeholder for loading state
  }

  // Assuming you have a form to update tour data
  return (
    <div>
      <h1>Update Tour</h1>
      <form>
        <label>Title:</label>
        <input type="text" defaultValue={tourData.title} />
        
        <label>City:</label>
        <input type="text" defaultValue={tourData.city} />
        
        <label>Photo:</label>
        <input type="text" defaultValue={tourData.photo} />
        
        <label>Price:</label>
        <input type="text" defaultValue={tourData.price} />
        
        {/* Add other fields as needed */}

        <button type="submit">Update Tour</button>
      </form>
    </div>
  );
};

export default UpdateTour;
