import React, { useEffect, useState, useContext } from 'react';
import { Container, Row, Col, ListGroup, ListGroupItem } from 'reactstrap';
import { AuthContext } from '../../context/AuthContext';
import { BASE_URL } from '../../utils/config';
import './booked-tours.css';

const BookedTours = () => {
  const { user } = useContext(AuthContext);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookings = async () => {
      console.log('User:', user); // Debugging line

      if (user && user._id) {
        try {
          const response = await fetch(`${BASE_URL}/booking/user/${user._id}`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include',
          });

          if (response.ok) {
            const result = await response.json();
            setBookings(result.data);
          } else {
            const errorData = await response.json();
            throw new Error(errorData.message || 'Failed to fetch bookings');
          }
        } catch (error) {
          console.error('Error fetching bookings:', error);
          setError(error.message);
        } finally {
          setLoading(false);
        }
      } else {
        setLoading(false); // Stop loading if user or user._id is not available
      }
    };

    fetchBookings();
  }, [user]);

  const handleCancelBooking = async (bookingId) => {
    const token = localStorage.getItem('token');

    try {
      const response = await fetch(`${BASE_URL}/booking/${bookingId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const updatedBookings = bookings.filter(booking => booking._id !== bookingId);
        setBookings(updatedBookings);
        alert('Booking cancelled successfully');
      } else {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to cancel booking');
      }
    } catch (error) {
      console.error('Failed to cancel booking:', error.message);
      alert(`Failed to cancel booking: ${error.message}`);
    }
  };

  if (loading) {
    return <Container className="booked-tours"><p>Loading...</p></Container>;
  }

  if (error) {
    return <Container className="booked-tours"><p>Error: {error}</p></Container>;
  }

  if (!user || !user._id) {
    return (
      <Container className="booked-tours">
        <p>Please log in to view your booked tours.</p>
      </Container>
    );
  }

  return (
    <Container className="booked-tours">
      <Row>
        <Col lg="12">
          <h2 className="mb-4">My Booked Tours</h2>
          {bookings.length === 0 ? (
            <p>You have not booked any tours yet.</p>
          ) : (
            <ListGroup>
              {bookings.map((booking) => (
                <ListGroupItem key={booking._id}>
                  <h5>{booking.tourName}</h5>
                  <p>Booked on: {new Date(booking.bookAt).toLocaleDateString()}</p>
                  <p>Guests: {booking.guestSize}</p>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleCancelBooking(booking._id)}
                  >
                    Cancel Booking
                  </button>
                </ListGroupItem>
              ))}
            </ListGroup>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default BookedTours;
