import React, { useEffect, useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { BASE_URL } from '../utils/config';

const BookedTours = () => {
  const { user } = useContext(AuthContext);
  const [tours, setTours] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookedTours = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve token from localStorage
        const response = await fetch(`${BASE_URL}/booking/user/${user._id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` // Include token in headers
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch booked tours');
        }

        const data = await response.json();
        setTours(data);
      } catch (err) {
        setError(err.message);
      }
    };

    if (user && user._id) {
      fetchBookedTours();
    }
  }, [user]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!user) {
    return <div>Please log in to view your booked tours.</div>;
  }

  return (
    <div>
      <h2>Booked Tours</h2>
      <ul>
        {tours.map((tour) => (
          <li key={tour._id}>{tour.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default BookedTours;
